function initMap(){var e=new google.maps.Map(document.getElementById("map"),{zoom:8,center:{lat:52.52,lng:13.405}}),o=new google.maps.Geocoder;document.getElementById("submit").addEventListener("click",function(){geocodeAddress(o,e)})}function geocodeAddress(e,o){var t=document.getElementById("address").value;e.geocode({address:t},function(e,t){if(t===google.maps.GeocoderStatus.OK){o.setCenter(e[0].geometry.location);var n=new google.maps.Marker({map:o,position:e[0].geometry.location})}else alert("Geocode was not successful for the following reason: "+t)})}